import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import {StroopWord_Default} from "./configFiles/StroopWord_Setup";
import {StroopColorWord_Default} from "./configFiles/StroopColorWord_Setup";
import {StroopColor_Default} from "./configFiles/StroopColor_Setup";
import {IntakeForm} from "./configFiles/IntakeForm_Setup";




//--------------------------------------------------------------
function FormSettings({ selectedItems, saveFormSettings }) {
  const { formName, id } = useParams();
  const navigate = useNavigate();

  // Select the correct default configuration based on the form name
  const getDefaultConfig = (formName) => {
    switch (formName) {
      case "Word":
        return StroopWord_Default;
      case "Color":
        return StroopColor_Default;
      case "WordColor":
        return StroopColorWord_Default;
      case "Intake Form":
        return IntakeForm;
      default:
        return {};
    }
  };

  // Set the default configuration as initial data
  const defaultSettings = getDefaultConfig(formName);
  const initialData = { id, name: formName, settings: defaultSettings };

  const [formData, setFormData] = useState(initialData);

  // Handle input changes
  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData({
      ...formData,
      settings: {
        ...formData.settings,
        [name]: type === "checkbox" ? checked : value,
      },
    });
  };

  const handleSave = () => {
    saveFormSettings(formData);
    navigate("/");
  };

  const renderFormFields = () => {
    return Object.entries(formData.settings).map(([key, value]) => (
      <div key={key} style={{ marginBottom: '10px' }}>
        {typeof value === "boolean" ? (
          <label>
            {key}:
            <select
              name={key}
              value={formData.settings[key] ? "true" : "false"}
              onChange={(e) =>
                handleChange({
                  target: { name: key, value: e.target.value === "true", type: "checkbox" },
                })
              }
            >
              <option value="true">True</option>
              <option value="false">False</option>
            </select>
          </label>
        ) : typeof value === "number" ? (
          <label>
            {key}:
            <input
              type="number"
              name={key}
              value={formData.settings[key]}
              onChange={handleChange}
            />
          </label>
        ) : Array.isArray(value) ? (
          <label>
            {key} (comma-separated values):
            <textarea
              name={key}
              value={formData.settings[key].join(", ")}
              onChange={(e) =>
                handleChange({
                  target: { name: key, value: e.target.value.split(",").map((val) => val.trim()) },
                })
              }
            />
          </label>
        ) : (
          <label>
            {key}:
            <input
              type="text"
              name={key}
              value={formData.settings[key] || ""}
              onChange={handleChange}
            />
          </label>
        )}
        <br />
      </div>
    ));
  };
  

  return (
    <div className="form-settings">
      <h2>{formName} Settings</h2>
      {renderFormFields()}
      <br />
      <button onClick={handleSave}>Save</button>
    </div>
  );
}

export default FormSettings;
