import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";

function FormSettings({ selectedItems, saveFormSettings }) {
  const { formName, id } = useParams(); // Get form name and unique ID from URL
  const navigate = useNavigate();

  // Find the current form from the selected items, or initialize a new form
  const form = selectedItems.find((item) => item.id === id) || {
    id,
    name: formName,
    imgSrc: `${formName.toLowerCase().replace(/\s/g, "_")}.png`,
    settings: { param1: "", param2: "" },
  };

  const [formData, setFormData] = useState(form);

  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      settings: {
        ...formData.settings,
        [name]: value,
      },
    });
  };

  // Save settings and navigate back to the home page
  const handleSave = () => {
    saveFormSettings(formData);
    navigate("/");
  };

  return (
    <div className="form-settings">
      <h2>{formName} Settings</h2>

      <label>
        Parameter 1:
        <input
          type="text"
          name="param1"
          value={formData.settings.param1}
          onChange={handleChange}
        />
      </label>
      <br />
      <label>
        Parameter 2:
        <input
          type="text"
          name="param2"
          value={formData.settings.param2}
          onChange={handleChange}
        />
      </label>
      <br />
      <button onClick={handleSave}>Save</button>
    </div>
  );
}


export default FormSettings;
