import React, { useState, useEffect } from "react";
import { useParams, useLocation, useNavigate } from "react-router-dom";

function FormSettings({ selectedItems, saveFormSettings }) {
  const { formId } = useParams(); // Get the unique form ID from the URL
  const location = useLocation();
  const navigate = useNavigate();

  // Get form info passed from the home component or find existing one
  const form = location.state?.form || selectedItems.find((item) => item.id === formId) || {
    name: "",
    settings: { param1: "", param2: "" },
  };

  // Local state to store form data
  const [formData, setFormData] = useState({
    ...form,
    id: formId, // Assign the unique form ID
  });

  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      settings: {
        ...formData.settings,
        [name]: value,
      },
    });
  };

  // Save settings and navigate back to the home page
  const handleSave = () => {
    saveFormSettings(formData);
    navigate("/");
  };

  return (
    <div className="form-settings">
      <h2>{form.name} Settings</h2>

      <label>
        Parameter 1:
        <input
          type="text"
          name="param1"
          value={formData.settings.param1}
          onChange={handleChange}
        />
      </label>
      <br />
      <label>
        Parameter 2:
        <input
          type="text"
          name="param2"
          value={formData.settings.param2}
          onChange={handleChange}
        />
      </label>
      <br />
      <button onClick={handleSave}>Save</button>
    </div>
  );
}

export default FormSettings;
