import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes, useNavigate } from "react-router-dom";
import Home from "./Home";
import FormSettings from "./FormSettings";
import './App.css';

function App() {
  const [selectedItems, setSelectedItems] = useState([]);

  // Add or update item in the list
  const saveFormSettings = (formData) => {
    const existingItem = selectedItems.find((item) => item.name === formData.name);
    if (existingItem) {
      // Update the form settings
      setSelectedItems(
        selectedItems.map((item) => (item.name === formData.name ? formData : item))
      );
    } else {
      // Add a new form
      setSelectedItems([...selectedItems, formData]);
    }
  };

  return (
    <Router>
      <Routes>
        {/* Home page with form selection */}
        <Route path="/" element={<Home selectedItems={selectedItems} />} />
        {/* Form settings page */}
        <Route
          path="/settings/:formName"
          element={<FormSettings selectedItems={selectedItems} saveFormSettings={saveFormSettings} />}
        />
      </Routes>
    </Router>
  );
}

export default App;
